import React, { Component } from 'react'
import { NavLink } from 'react-router-dom'
import logo from './logo.svg'
import './App.css'

class Drinker extends Component {
  state = {
    name: '',
    transactions: []
  }

  componentDidMount () {
    // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ transactions: res }))
      .catch(err => console.log(err))

    const { handle } = this.props.match.params

    fetch(`/drinkers/${handle}`).then(user => {
      this.setState(() => ({ user }))
    })
  }
  // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('/transactions/${name}')
    const body = await response.json()

    if (response.status !== 200) {
      throw Error(body.message)
    }

    return body
  }

  render () {
    return (
      <div>
        <table className='table'>
          <thead>
            <tr className='tr'>
              <th className='th'>Transaction</th>
            </tr>
          </thead>
          <tbody>
            {this.state.transactions.map(transaction => (
              <tr key={transaction.Id} className='tr'>
                <td className='td'>{transaction.Time}</td>
                <td className='td'>{transaction.Total}</td>
                <td className='td'>{transaction.Bar}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    )
  }
}

export default Drinker
